function runPSystem(){for(var t=0;t<particles.length;t++)particles[t].run()}function particle(t,i,s,n){this.x=t,this.y=i,this.w=s,this.h=n,this.rotation=0,this.display=function(){stroke(0),push(),translate(this.x,this.y),rectMode(CENTER),rotate(radians(this.rotation)),rect(0,0,this.w,this.h),pop()},this.turn=function(){this.rotation++},this.move=function(){var t=this.x,i=this.y;t+=random(-1,1),i+=random(-1,1),this.x=constrain(t,0,displayWidth),this.y=constrain(i,0,displayHeight)},this.update=function(){this.turn(),this.move()},this.run=function(){this.display(),this.update()}}function cls(){rectMode(CORNER),noStroke(),rect(0,0,width,height)}function setup(){createCanvas(displayWidth,displayHeight)}function draw(){cls(),runPSystem()}function mousePressed(){particles.push(new particle(mouseX,mouseY,10,10))}var particles=[];